mapper.select:
SELECT * FROM ( SELECT @RN:=@RN+1 AS rnum, a.* FROM(
SELECT * FROM bbs_t WHERE status = 0 and bname = #{bname} order by b_idx DESC
) a, ( SELECT @RN:=0 ) b
) c WHERE c.rnum BETWEEN #{begin} and #{end}

mapper.update:
UPDATE bbs_t

    subject = #{subject}, content = #{content}, ip = #{ip}

        ori_name = #{ori_name},
        file_name = #{file_name}

    WHERE b_idx = #{b_idx}
