mapper.select:
select * from(
    select @RN:=@RN+1 as rnum, a.* from(
        select * from bbs_t
        where status = 0 and bname = #{bname}
        order by b_idx desc
    ) a, (select @RN:=0) b
) c WHERE c.rnum BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE bbs_t
SET status = 1
WHERE b_idx = #{bIdx}

UPDATE bbs_t
SET hit = hit + 1
WHERE b_idx = #{bIdx}

UPDATE bbs_t
SET subject = #{subject},
writer = #{writer},
content = #{content},
file_name = #{fname}
WHERE b_idx = #{b_idx}
